//slideshow
import React from "react";
import './glimpses.css';

export default function Glimpses()
{
let slideIndex = 1;
showSlides(slideIndex);

function plusSlides(n) {
  showSlides(slideIndex += n);
}

function currentSlide(n) {
  showSlides(slideIndex = n);
}

function showSlides(n) {
  let i;
  let slides = ['1.jpg','2.jpg','3.jpg','4.jpg','5.jpg','6.jpg','7.jpg','8.jpg','9.jpg']

  if (n > slides.length) {slideIndex = 1}    
  if (n < 1) {slideIndex = slides.length}
  for (i = 0; i < slides.length; i++) {
    slides[i].style.display = "none";  
  }
  for (i = 0; i < dots.length; i++) {
    dots[i].className = dots[i].className.replace(" active", "");
  }
  slides[slideIndex-1].style.display = "block";  
  dots[slideIndex-1].className += " active";
}
    return (
        <div>
            <div className="mySlides fade"><img src="1.jpg"/></div>
            <div className="mySlides fade"><img src="2.jpg"/></div>
            <div className="mySlides fade"><img src="3.jpg"/></div>
            <div className="mySlides fade"><img src="4.jpg"/></div>
            <div className="mySlides fade"><img src="5.jpg"/></div>
            <div className="mySlides fade"><img src="6.jpg"/></div>
            <div className="mySlides fade"><img src="7.jpg"/></div>
            <div className="mySlides fade"><img src="8.jpg"/></div>
            <div className="mySlides fade"><img src="9.jpg"/></div>
            <a className="prev" onclick={plusSlides(-1)}>❮</a>
            <a className="next" onclick={plusSlides(1)}>❯</a>

            <div style="text-align:center">
                <span className="dot" onclick={currentSlide(1)}></span> 
                <span className="dot" onclick={currentSlide(2)}></span> 
                <span className="dot" onclick={currentSlide(3)}></span> 
                <span className="dot" onclick={currentSlide(4)}></span> 
                <span className="dot" onclick={currentSlide(5)}></span> 
                <span className="dot" onclick={currentSlide(6)}></span> 
                <span className="dot" onclick={currentSlide(7)}></span> 
                <span className="dot" onclick={currentSlide(8)}></span> 
                <span className="dot" onclick={currentSlide(9)}></span> 
            </div>
           
        </div>
    )
}
