//slideshow
import React from "react";
import './glimpses.css';

export default function Glimpses()
{
    let slideIndex = 1;
showSlides(slideIndex);

function plusSlides(n) {
  showSlides(slideIndex += n);
}

function currentSlide(n) {
  showSlides(slideIndex = n);
}

function showSlides(n) {
  let i;
  let slides = document.getElementsByClassName("mySlides");
  let dots = document.getElementsByClassName("dot");
  if (n > slides.length) {slideIndex = 1}    
  if (n < 1) {slideIndex = slides.length}
  for (i = 0; i < slides.length; i++) {
    slides[i].style.display = "none";  
  }
  for (i = 0; i < dots.length; i++) {
    dots[i].className = dots[i].className.replace(" active", "");
  }
  slides[slideIndex-1].style.display = "block";  
  dots[slideIndex-1].className += " active";
}
    return (
        <div>
           <img className="mySlides" src="1.jpg"/>
           <img className="mySlides" src="2.jpg"/>
           <img className="mySlides" src="3.jpg"/>
           <img className="mySlides" src="4.jpg"/>
           <img className="mySlides" src="5.jpg"/>
           <img className="mySlides" src="6.jpg"/>
           <img className="mySlides" src="7.jpg"/>
           <img className="mySlides" src="8.jpg"/>
           <img className="mySlides" src="9.jpg"/>
           <a className="prev" onclick={plusSlides(-1)}>❮</a>
           <a className="next" onclick={plusSlides(1)}>❯</a>
        </div>
    )
}
