import React from "react";

export default function Glimpses()
{
    let max = 9;
    let [index, setIndex] = React.useState(0);
    function next(){
        setIndex((prev)=>prev+1);
    }
    function prev(){
        setIndex((prev)=>prev-1);
    }
    return (
        <div>
            <img src={`./images/${index}.jpg`}/>
        </div>
    )
}








