//slideshow
import React, { useState } from "react";
import './glimpses.css';

export default function Glimpses()
{
let [slideIndex, setSlideIndex] = useState(0);
let slides = ['1.jpg','2.jpg','3.jpg','4.jpg','5.jpg','6.jpg','7.jpg','8.jpg','9.jpg']

    return (
        <div>          
            <div><img src={`./images/${slides[slideIndex]}`}/></div>
            <a className="prev" onclick={()=>{ if (slideIndex < 0) {return setSlideIndex(0)} return setSlideIndex(slideIndex - 1)}}>❮</a>
            <a className="next" onclick={()=>{if( slideIndex < slides.length){return setSlideIndex(slides.length-1)} return setSlideIndex(slideIndex + 1)}}>❯</a>
        </div>
    )
}
