// //slideshow
// import React, { useState } from "react";
import './glimpses.css';
let slides = ['1.jpg','2.jpg','3.jpg','4.jpg','5.jpg','6.jpg','7.jpg','8.jpg','9.jpg']

export default function Glimpses()
{
    let [slideIndex, setSlideIndex] = useState(0);
    
    function next(){
        setSlideIndex((prevslideIndex) => { return prev+slideIndex+ 1})
    }
    // {if(slideIndex >= slides.length){return 0}
    function prev(){
        setSlideIndex((prevslideIndex) => { return prevslideIndex-1})
    }
    // if(slideIndex <= 0){return slides.length}
    return (
        <div>          
            <img src={`./images/${slideIndex+1}.jpg`}/>
            <div className="prev" onClick={prev}>❮</div>
            <div className="next" onClick={next}>❯</div>
        </div>
    )
}
