//slideshow
import React, { useState } from "react";
import './glimpses.css';
let slides = ['1.jpg','2.jpg','3.jpg','4.jpg','5.jpg','6.jpg','7.jpg','8.jpg','9.jpg']

export default function Glimpses()
{
    let [slideIndex, setSlideIndex] = useState(0);
    
    function next(){
        setSlideIndex((slideIndex) => {if(slideIndex >= slides.length){return 0} return slideIndex+ 1})
    }
    function prev(){
        setSlideIndex((slideIndex) => {if(slideIndex <= 0){return slides.length} return slideIndex-1})
    }
    return (
        <div>          
            <img src={`./images/${slideIndex+1}.jpg`}/>
            <a className="prev" onclick={prev}>❮</a>
            <a className="next" onclick={next}>❯</a>
        </div>
    )
}
